pragma solidity ^0.4.25;

contract Token{

    //uint256 public totalSupply;

    function balanceOf(address _owner) view public returns (uint256 balance);

    function transfer(address _to, uint256 _value) public returns (bool success);

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success);

    function approve(address _spender, uint256 _value) public returns (bool success);

    function allowance(address _owner, address _spender) public view returns (uint256 remaining);

    event Transfer(address indexed _from, address indexed _to, uint256 _value);

    event Approval(address indexed _owner, address indexed _spender, uint256 _value);
}

contract StandardToken is Token {
    mapping (address => uint256) balances;
    mapping (address => mapping (address => uint256)) allowed;

    function transfer(address _to, uint256 _value) public returns (bool success) {

        require(balances[msg.sender] >= _value);
        balances[msg.sender] -= _value;
        balances[_to] += _value;
        emit Transfer(msg.sender, _to, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        //require(balances[_from] >= _value && allowed[_from][msg.sender] >= 
        // _value && balances[_to] + _value > balances[_to]);
        require(balances[_from] >= _value && allowed[_from][msg.sender] >= _value);
        balances[_to] += _value;
        balances[_from] -= _value;
        allowed[_from][msg.sender] -= _value;
        emit Transfer(_from, _to, _value);

        return true;
    }

    function balanceOf(address _owner) view public returns (uint256 balance) {
        return balances[_owner];
    }


    function approve(address _spender, uint256 _value) public returns (bool success)   
    {
        allowed[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }


    function allowance(address _owner, address _spender) view public returns (uint256 remaining) {
        return allowed[_owner][_spender];
    }

}

contract HumanStandardToken is StandardToken { 

    /* Public variables of the token */
    string public name;
    uint8 public decimals;
    string public symbol;
    string public version = 'HW0.1';
    address public deployer;

    uint256 public totalNum;

    struct article {
        string Headline;
        address Author;
        uint32 Class;
        string IPFSAddress;
        uint256 like;
        uint256 unlike;
    }

    mapping(uint256 => article) allArticles;
    mapping(address => uint256[]) ownedArticle;
    mapping(uint32 => uint256[]) classArticle;

    constructor(uint8 _decimalUnits) public {
        deployer = msg.sender;
        balances[msg.sender] = 10000000000;
        //totalSupply = 10000000000;
        name = "HW_token";
        decimals = _decimalUnits;
        symbol = "HW";
    }

    function post(string headline, uint32 class, string ipfsAddress) public {
        transfer(deployer, 100);
        totalNum = totalNum +1;
        classArticle[class].push(totalNum);
        ownedArticle[msg.sender].push(totalNum);

        allArticles[totalNum].Headline = headline;
        allArticles[totalNum].Author = msg.sender;
        allArticles[totalNum].Class = class;
        allArticles[totalNum].IPFSAddress = ipfsAddress;
    }

    function readById(uint256 articleID) view public returns(string, address, uint32, string, uint256, uint256) {
        return ( allArticles[articleID].Headline,
        allArticles[articleID].Author,
        allArticles[articleID].Class,
        allArticles[articleID].IPFSAddress,
        allArticles[articleID].like,
        allArticles[articleID].unlike);
    }

    function getOwnedArticle (address owner) view public returns(uint256[]) {
        return ownedArticle[owner];
    }

    function like (uint256 articleID) public returns (uint256) {
        require(totalNum >= articleID);
        uint256 guest = rand;
        uint256 isOdd = guest % 2;
        allArticles[articleID].like += 1;
        transfer(allArticles[articleID].Author, 1);
        balances[msg.sender] += 10*isOdd;
        return isOdd;
    }

     function unlike (uint256 articleID) public returns (uint256) {
        require(totalNum >= articleID);
        uint256 guest = rand;
        uint256 isOdd = guest % 2;
        allArticles[articleID].unlike += 1;
        transfer(deployer, 1);
        balances[msg.sender] += 10*isOdd;
        return isOdd;
    }
}
